def getASCIIchar(codestr, curPos):
    moveCnt = codestr.find('.')
    if (moveCnt >= 0):
        curPos += moveCnt 
        curPos %= 256
        if moveCnt+1 == len(codestr):
            return chr(curPos) 
        else:
            return chr(curPos) + getASCIIchar(codestr[moveCnt+1:], curPos)
    else:
        # moveCnt = len(codestr)
        # return chr(curPos)
        return ''
          


def my_first_interpreter(code):
    #TODO 1: remove comments
    newCode = ''.join([c for c in code if c in '+.'])
    #TODO 2: count number of '+' (index)
    result = ''
    result += getASCIIchar(newCode, 0)
    return result


